import React, { FC } from 'react';
import './GridComponent.scss'; // Assuming you have a Sass file for styling

interface GridProps {
  numRows: number;
  numColumns: number;
}

const GridComponent: FC<GridProps> = ({ numRows, numColumns }) => {
  const rows = Array.from({ length: numRows });

  const columns = Array.from({ length: numColumns });

  return (
    <div className="grid-container">
      {/* Title Row */}
      <div className="grid-item title" style={{ gridArea: `1 / 1 / 2 / ${numColumns + 1}` }}>
        <p>This is the Title</p>
      </div>

      {/* Cells */}
      {rows.map((_, rowIndex) =>
        columns.map((_, columnIndex) => (
          <div
            key={`${rowIndex}-${columnIndex}`}
            className="gridItem"
            style={{ gridArea: `${rowIndex + 2} / ${columnIndex + 1} / ${rowIndex + 3} / ${columnIndex + 2}` }}
          >
            <h2>Cell {rowIndex * numColumns + columnIndex + 1} Title</h2>
            <p>Cell {rowIndex * numColumns + columnIndex + 1} Content</p>
          </div>
        ))
      )}
    </div>
  );
};

export default GridComponent;
