import React, { FC } from 'react';
import './GridComponent.scss';

interface GridProps {
  numRows: number;
  numColumns: number;
  showHeader?: boolean;
}

const GridComponent: FC<GridProps> = ({ numRows, numColumns, showHeader = true }) => {
  const rows = Array.from({ length: numRows });

  const columns = Array.from({ length: numColumns });

  return (
    <div className="gridContainer">
    {showHeader && (
        <div className="gridItemHeader"></div>
    )}
      {/* Cells */}
      {rows.map((_, rowIndex) =>
        columns.map((_, columnIndex) => (
          <div
            key={`${rowIndex}-${columnIndex}`}
            className="gridItem"
            style={{ gridArea: `${rowIndex + 2} / ${columnIndex + 1} / ${rowIndex + 3} / ${columnIndex + 2}` }}
          >
            <h2>Cell {rowIndex * numColumns + columnIndex + 1} Title</h2>
            <p>Cell {rowIndex * numColumns + columnIndex + 1} Content</p>
          </div>
        ))
      )}
    </div>
  );
};

export default GridComponent;
