import React, { useState, useEffect } from 'react';
import './TopAdoptionRates.scss';
import PercentCircle from './PercentCircle';
import DynamicGrid from './DynamicGrid';
import TextDisplay from './TextDisplay';
import SimpleGrid from'./SimpleGrid';
import './_variables.scss'

interface AdoptionRate {
  'Template': string;
  'Adoption Rate': string;
  'Total Users': string;
  'Adopted count': string;
  'Growth Rate': string;
}

function TopAdoptionRates() {

    // I assume data won't really have adoption rate provided and this will be something we need to calculate using adopted count / total users 
    // but for easy demosntration, will include it for the time being
  const templateData = [
    { 'Template': 'fly-docker-image', 'Adoption Rate': '80%', 'Total Users': '20', 'Adopted count': '16', 'Growth Rate': '5%' },
    { 'Template': 'Develop in Docker', 'Adoption Rate': '50%', 'Total Users': '20', 'Adopted count': '10', 'Growth Rate': '65%' },
    { 'Template': 'Develop in Docker-2', 'Adoption Rate': '30%', 'Total Users': '20', 'Adopted count': '6', 'Growth Rate': '15%' }
  ];


  const calculateAdoptionRate = (totalUsers: number, adoptionCount: number) => {
    return (adoptionCount / totalUsers) * 100;
  };

    // sort data in descending order (highest first)
    const sortByHighestAdoptionRate = (data: AdoptionRate[]): AdoptionRate[] => {

      return data.sort((a, b) => {
        // Parse the Adoption Rate percentage and remove the '%' sign
        const adoptionRateA = parseFloat(a['Adoption Rate'].replace('%', ''));
        const adoptionRateB = parseFloat(b['Adoption Rate'].replace('%', ''));

        return adoptionRateB - adoptionRateA;
      });
    };

  // Api call available for fetching data, if not implemented just use sample data
  const useDataFromApi = (apiUrl: string) => {
    const [data, setData] = useState<AdoptionRate[]>([]);

    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await fetch(apiUrl);
          if (response.ok) {
            const jsonData = await response.json();
            setData(jsonData);
          } else {
            setData(templateData);
          }
        } catch (error) {
          setData(templateData);
        }
      };

      fetchData();
    }, [apiUrl]);

    return data;
  };

  const getHighestAdoptionRate = (data: AdoptionRate[]): AdoptionRate | string => {
    const sortedData = sortByHighestAdoptionRate(data);

    if (sortedData.length > 0) {
      return sortedData[0];
    }

    return '';
  };

  const [data, setData] = useState(templateData);
  const [topAdoptionRate, setTopAdoptionRate] = useState(getHighestAdoptionRate(data));
  console.log('top', topAdoptionRate);

  return (
    <div className="top-adoption-rates">
      <div className={"title"}>
        <h4>Top Template Adoption Rates</h4>
      </div>
      <div className={"top-adoption-percent"}>
      <PercentCircle
          title={"Adoption Rate"}
          titleColor={'#b4bfd5'}
          percentage={80}
          size={200}
          strokeWidth={10}
          circleColor={"#305bf5"}
          textColor={"#fff"}
          emptyColor={"#1d2435"}
        />
        <div className="top-template-detail">
          <TextDisplay title={"Most Adopted Template"} textContent={"fly-docker-image"} /> 
          <SimpleGrid data={Object.entries(topAdoptionRate).slice(1).map(([key, value]) => ({
            title: key,
            content: value,
          }))} />
        </div>
        </div>
        <div className="template-adoption-grid">
        <DynamicGrid data={templateData} hasHeaders/>           
        </div> 
        <a href="/" className="link">See More</a>
    </div>
  );
}

export default TopAdoptionRates;
