import React from 'react';

interface PercentCircleProps {
  title: string;
  percentage: number;
  size: number;
  strokeWidth: number; 
  titleColor: string; 
  circleColor: string; 
  textColor: string; 
}

const PercentCircle: React.FC<PercentCircleProps> = ({
  title,
  percentage,
  size,
  strokeWidth,
  titleColor,
  circleColor,
  textColor,
}) => {
  // Calculate the radius of the circle
  const radius = (size - strokeWidth) / 2;

  // Calculate the circumference of the circle
  const circumference = 2 * Math.PI * radius;

  // Calculate the stroke dash offset to show the correct percentage
  const strokeDashoffset = circumference - (percentage / 100) * circumference;

  return (
    <svg width={size} height={size} viewBox={`0 0 ${size} ${size}`}>
      <circle
        cx={size / 2}
        cy={size / 2}
        r={radius}
        fill="transparent"
        stroke={circleColor}
        strokeWidth={strokeWidth}
        strokeDasharray={circumference}
        strokeDashoffset={strokeDashoffset}
      />
      { title && 
      <text 
      x="30%"
      y="50%"
      textAnchor="middle"
      fill={titleColor} 
      fontSize={size/16}
      > {title} 
      </text>}
      <text
        x="60%"
        y="60%"
        textAnchor="middle"
        fill={textColor}
        fontSize={size / 4}
        fontWeight="bold"
      >
        {percentage}%
      </text>
    </svg>
  );
};

export default PercentCircle;
