import React from 'react';
import './DynamicGrid.scss'; // Assuming you have a Sass file for styling

interface GridData {
  [key: string]: string;
  hasHeaders: boolean;
}

interface DynamicGridProps {
  data: GridData[];
}

const DynamicGrid: React.FC<DynamicGridProps> = ({ data, hasHeaders = true }) => {
  // Extract the unique keys (headings) from the data objects
  const headings = data.length > 0 ? Object.keys(data[0]) : [];

  //{ Name: 'John Doe', Age: '30', Email: 'johndoe@example.com' },

  return (
    <div className="dynamic-grid">
      {/* Render the header row with dynamically populated columns */}
      <div className="grid-row header">
        {headings.map((heading, columnIndex) => (
          <div key={columnIndex} className="grid-heading">
            {heading}
          </div>
        ))}
      </div>

      {/* Render content for each row */}

      {data.map((row, rowIndex) => (
        <div key={rowIndex} className="grid-row">
          {headings.map((heading, columnIndex) => (
            <div key={columnIndex} className="grid-content">
              {row[heading]}
            </div>
          ))}
        </div>
      ))}


    </div>
  );
};

export default DynamicGrid;
