import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './TopAdoptionRates.scss';
import PercentCircle from './PercentCircle';
import DynamicGrid from './DynamicGrid';
import TextDisplay from './TextDisplay';
import './_variables.scss'

interface AdoptionRate {
  'Template': string;
  'Adoption Rate': string;
  'Total Users': string;
  'Adopted count': string;
  'Growth Rate': string;
}

function TopAdoptionRates() {

    // sample data
  const gridData = [
    { 'Template': 'fly-docker-image', 'Adoption Rate': '80%', 'Total Users': '200', 'Adopted count': '160', 'Growth Rate': '5%' },
    { 'Template': 'Develop in Docker', 'Adoption Rate': '50%', 'Total Users': '200', 'Adopted count': '100', 'Growth Rate': '65%' },
    { 'Template': 'Develop in Docker-2', 'Adoption Rate': '30%', 'Total Users': '200', 'Adopted count': '60', 'Growth Rate': '5%' }
  ];

  const templateData = [
    { 'Template': 'fly-docker-image', 'Total Users': '200', 'Adopted count': '160', 'Growth Rate': '5%' },
    { 'Template': 'Develop in Docker', 'Total Users': '200', 'Adopted count': '100', 'Growth Rate': '65%' },
    { 'Template': 'Develop in Docker-2','Total Users': '200', 'Adopted count': '60', 'Growth Rate': '5%' }
  ];

  const calculateAdoptionRate = (totalUsers: number, adoptionCount: number) => {
    return (adoptionCount / totalUsers) * 100;
  };

  const calculateAdoptionRatesAndSort = (templateData: any[]): AdoptionRate[] => {
    // Calculate the Adoption Rate and add it as a new property to each object
    const dataWithAdoptionRate = templateData.map((item) => {
      const totalUsers = parseFloat(item['Total Users']);
      const adoptionCount = parseFloat(item['Adopted count']);

      return {
        ...item,
        AdoptionRate: calculateAdoptionRate(totalUsers, adoptionCount),
      };
    });
    // Sort the data based on the calculated Adoption Rate (in ascending order)
  const sortedData = dataWithAdoptionRate.sort((a, b) => a.AdoptionRate - b.AdoptionRate);

  return sortedData;
};





    // sort data in descending order (highest first)
    const sortByHighestAdoptionRate = (data: AdoptionRate[]): AdoptionRate[] => {

      return data.sort((a, b) => {
        // Parse the Adoption Rate percentage and remove the '%' sign
        const adoptionRateA = parseFloat(a['Adoption Rate'].replace('%', ''));
        const adoptionRateB = parseFloat(b['Adoption Rate'].replace('%', ''));

        return adoptionRateB - adoptionRateA;
      });
    };


  const useDataFromApi = (apiUrl: string) => {
    const [data, setData] = useState<AdoptionRate[]>([]);

    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await fetch(apiUrl);
          if (response.ok) {
            const jsonData = await response.json();
            setData(jsonData);
          } else {
            // If API call fails, use sample data as fallback
            setData(gridData);
          }
        } catch (error) {
          // If API call fails, use sample data as fallback
          setData(gridData);
        }
      };

      fetchData();
    }, [apiUrl]);

    return data;
  };

  const getHighestAdoptionRate = (data: AdoptionRate[]): AdoptionRate | string => {
    const sortedData = sortByHighestAdoptionRate(data);

    if (sortedData.length > 0) {
      // Return the first object in the sorted array (highest adoption rate)
      return sortedData[0];
    }

    return 'No Data';
  };

  const [data, setData] = useState(gridData);
  const [topAdoptionRate, setTopAdoptionRate] = useState(getHighestAdoptionRate(data));

  return (
    <div className="top-adoption-rates">
      <div className={"title"}>
        <h4>Top Template Adoption Rates</h4>
      </div>
      <div className={"top-adoption-percent"}>
      <PercentCircle
          title={"Adoption Rate"}
          titleColor={'#b4bfd5'}
          percentage={80}
          size={150}
          strokeWidth={10}
          circleColor={"#305bf5"}
          textColor={"#fff"}
          emptyColor={"#1d2435"}
        />
        <TextDisplay title={"Most Adopted Template"} textContent={"fly-docker-imagea"} /> 
        <DynamicGrid data={gridData} hasHeaders={false}/>
        </div>
        <DynamicGrid data={gridData} hasHeaders/>            

    </div>
  );
}

export default TopAdoptionRates;
